import React from 'react';
import TodoList from './TodoList';
import AddTodo from './AddTodo';
import { Paper, List, Container,Grid,Button,AppBar,Toolbar,Typography }  from "@material-ui/core";
import '../../css/App.css';
import { call,signout } from '../service/ApiService';

var todoURL = "/todo";

class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
       items : [],
      };
  }
  
  componentDidMount() {
    call(todoURL, "GET", null).then((response) =>
      this.setState({ items: response.data })
    );
  }

  add = (item) => {
    call(todoURL, "POST", item).then((response) =>
      this.setState({ items: response.data })
    );
  };

  delete = (item) => {
    call(todoURL, "DELETE", item).then((response) =>
      this.setState({ items: response.data })
    );
  };

  update = (item) => {
    call(todoURL, "PUT", item).then((response) =>
      this.setState({ items: response.data })
    );
  };

  render() {
    var todoItems = this.state.items.length > 0 && (
      <Paper style={{ margin: 16 }}>
        <List>
          {this.state.items.map((item, idx) => (
            <TodoList
              item={item}
              key={item.id}
              delete={this.delete}
              update={this.update}
            />
          ))}
        </List>
        
      </Paper>
    );
    //navigationBar 추가
    var navigationBar = (
      <AppBar position='static'>
      <Toolbar>
      <Grid justify="space-between" container>
          <Grid item>
            <Typography variant="h6">오늘의 할일 :: TO DO</Typography>
          </Grid>
          <Grid>
            <Button color="inherit" onClick={signout}>
              로그아웃
            </Button>
          </Grid>
        </Grid>
      </Toolbar>
        
      </AppBar>
    );
    //props로 넘겨주기
    return (
      <div className="App">
        {navigationBar}{/*내비게이션 바 렌더링*/}
        <Container maxWidth="md">
          <AddTodo add={this.add} />
          <div className="TodoList">{todoItems}</div>
        </Container>
      </div>
    );
  }
}

 

export default App;
